public function EvtOnStateTransition(Combodo\iTop\Service\Events\EventData $oEventData)
{
    $sTo = (string) $oEventData->Get('to_state');

    if ($sTo === 'in_progress' && empty($this->Get('started_on'))) {
        $this->Set('started_on', date('Y-m-d'));
    }
    if ($sTo === 'completed' && empty($this->Get('completed_on'))) {
        $this->Set('completed_on', date('Y-m-d'));
    }
    if ($sTo === 'completed' && method_exists($this, 'OnReviewCompleted')) {
        $this->OnReviewCompleted();
    }
}

public function EvtReviewCheckToWrite(Combodo\iTop\Service\Events\EventData $oEventData)
{

  $sCurrentStatus = (string) $this->Get('status');
  $sTargetState   = (string) $oEventData->Get('target_state'); // '' or 'planned'|'in_progress'|'completed'|'cancelled'
  $sStatusAfter   = $sTargetState !== '' ? $sTargetState : $sCurrentStatus;

  if (($oEventData->Get('is_new') === true) || ($sStatusAfter === 'planned'))
  {
      $sPlanned = (string) $this->Get('planned_on'); // 'Y-m-d'
      if ($sPlanned !== '')
      {
          $tsPlanned = @strtotime($sPlanned);
          $tsToday   = @strtotime(date(AttributeDate::GetInternalFormat())); // 'Y-m-d' today
          if ($tsPlanned !== false && $tsPlanned < $tsToday)
          {
            $this->AddCheckIssue(Dict::S('Class:ISMSReview/Check:PlannedInPast'));
          }
      }
  }

  $sTarget = (string) $oEventData->Get('target_state');
  if ($sTarget === 'completed' && empty($this->Get('outcome'))) {
    $this->AddCheckIssue(Dict::S('Class:ISMSReview/Check:NoOutcome'));
  }
}

/**
* PrefillCreationForm
* - Default planned_on = today
* - If empty, preset reviewer to the asset owner (if present)
*/
public function PrefillCreationForm(&$aContextParam)
{
    if (empty($this->Get('planned_on'))) {
        $this->Set('planned_on', date('Y-m-d'));
    }
    // reviewer = Asset.owner (falls vorhanden)
    try {
        $iAssetId = (int)$this->Get('asset_id');
        if ($iAssetId > 0 && empty($this->Get('reviewer_id'))) {
            $oAsset = MetaModel::GetObject('ISMSAsset', $iAssetId, false);
            if ($oAsset) {
                $iOwner = (int)$oAsset->Get('assetowner_id');
                if ($iOwner > 0) {
                   $this->Set('reviewer_id', $iOwner);
                }
            }
        }
    } catch (\Exception $e) { /* ignore */ }
}

public function GetTargetObject()
{
    $iId = (int)$this->Get('asset_id');
    return ($iId > 0) ? MetaModel::GetObject('ISMSAsset', $iId, false) : null;
}

public function EvtAssetReviewCheckToWrite(Combodo\iTop\Service\Events\EventData $oEventData)
{
    // Zeitliche Konsistenz
    $sPlanned = (string) $this->Get('planned_on');
    $sStarted = (string) $this->Get('started_on');
    $sCompleted = (string) $this->Get('completed_on');
    if (!empty($sStarted) && !empty($sPlanned) && $sStarted < $sPlanned) {
        $this->AddCheckIssue(Dict::S('Class:ISMSReview/Check:StartedOnIsBeforePlannedOn'));
    }
    if (!empty($sCompleted) && (!empty($sStarted) && $sCompleted < $sStarted)) {
        $this->AddCheckIssue(Dict::S('Class:ISMSReview/Check:StartedOnIsBeforePlannedOn'));
    }
}

public function EvtAssetReviewAfterWrite(Combodo\iTop\Service\Events\EventData $oEventData)
{
    $sStimulus = (string) $oEventData->Get('stimulus_applied'); // z. B. 'ev_complete'
    $sTarget   = (string) $oEventData->Get('target_state');     // z. B. 'completed'

    if ($sStimulus === 'ev_complete') {

        $oAsset = $this->GetTargetObject();
        if ($oAsset) {
            $sCompleted = $this->Get('completed_on');
            if (empty($sCompleted)) {
                $sCompleted = date('Y-m-d');
            }

            // last_review aktualisieren
            $oAsset->Set('last_review', $sCompleted);

            // Intervall holen (Fallback 12), next_review berechnen
            $iMonths = (int)$oAsset->Get('review_interval_months');
            if ($iMonths <= 0) {
                $iMonths = 12;
            }

            $ts = strtotime('+'.$iMonths.' months', strtotime($sCompleted));
            $oAsset->Set('next_review', date('Y-m-d', $ts));

            $oAsset->DBUpdate();
        }
    }
}

